"use strict";var loginBtn=document.querySelector("#login-btn"),signupBtn=document.querySelector("#signup-btn");function login(){var t=document.querySelector("#login-email"),n=document.querySelector("#login-password"),e={email:t.value,password:n.value},o={method:"POST",body:JSON.stringify(e),headers:{Accept:"application/json, text/plain, /","Content-Type":"application/json"}};fetch("/api/auth/login",o).then(function(e){200==e.status?location.assign("/profile"):(M.toast({html:"Could not log you in. Please make sure you have entered the correct email address and password for your account."}),t.classList.add("invalid"),n.classList.add("invalid"))})}function signup(){var e=document.querySelector("#signup-firstname"),t=document.querySelector("#signup-lastname"),n=document.querySelector("#signup-email"),o=document.querySelector("#signup-password1"),a=document.querySelector("#signup-password2"),l=document.querySelector("#signup-role");if(a.value===o.value)if(""!==e.value&&""!==t.value&&""!==n.value&&""!==o.value){var s={firstname:e.value,lastname:t.value,email:n.value,password:o.value,role:l.options[l.selectedIndex].value},i={method:"POST",body:JSON.stringify(s),headers:{Accept:"application/json, text/plain, /","Content-Type":"application/json"}};fetch("/api/user",i).then(function(e){200==e.status?location.assign("/profile"):M.toast({html:"Could not sign you up. Do you already have an account?"})})}else M.toast({html:"Remeber to fill in all the fields"});else M.toast({html:"Looks like your passwords are not the same"})}loginBtn.addEventListener("click",login),signupBtn.addEventListener("click",signup);